mapper.select:
SELECT learn_sttus_cd
FROM LEARN_HIST
WHERE learn_hist_no = #{hist_no}

SELECT infr_sttus_cd
FROM INFERENCE_HIST
WHERE infr_hist_no = #{hist_no}

SELECT CAST(LH.learn_hist_no AS CHAR) AS learn_hist_no , CAST(LH.param_id AS CHAR) AS param_id , CAST(LH.alg_id AS CHAR) AS alg_id , CAST(LH.dp_analysis_id AS CHAR) AS dp_analysis_id , CAST(LH.project_id AS CHAR) AS project_id , DI.data_analysis_json , LH.learn_sttus_cd , LH.start_time , LH.end_time , LH.ml_result_json , LH.ml_score , LH.eps , LH.issue_task_idx , LH.model_alias , LH.model_bookmark , MPI.param_json , MAI.alg_json , MAI.metadata_json , MAI.alg_type , MAI.alg_json , AI.alg_nm , AI.algorithm_code , AI.alg_type , AI.dist_yn , AI.alg_cont , AI.alg_ver , AI.proc_id , AI.proc_dt , AI.lib_type , AI.user_made_yn FROM LEARN_HIST LH INNER JOIN ML_PARAM_INFO MPI ON LH.param_id = MPI.param_id INNER JOIN ALG_ANAL_INFO MAI ON LH.alg_anal_id = MAI.alg_anal_id INNER JOIN ALGORITHM_INFO AI ON MAI.alg_id = AI.alg_id INNER JOIN DP_ANLS_INFO DI ON LH.dp_analysis_id = DI.dp_analysis_id WHERE LH.project_id = #{project_id}
AND learn_hist_no IN
#{item}

SELECT status
FROM PROJECT_INFO
WHERE project_id=#{project_id}

SELECT eval_result_json
FROM LEARN_HIST
WHERE learn_hist_no = #{learnHistNo}

SELECT learn_hist_no, param_id, alg_anal_id, alg_id, dp_analysis_id, project_id
FROM LEARN_HIST
WHERE learn_hist_no = #{learnHistNo}

SELECT logs
FROM INFERENCE_HIST
WHERE infr_hist_no = #{infr_hist_no}

mapper.update:
UPDATE LEARN_HIST
SET    learn_sttus_cd = #{sttus_cd}, issue_task_idx = #{task_idx}
WHERE  learn_hist_no = #{hist_no}

UPDATE INFERENCE_HIST
SET    infr_sttus_cd = #{sttus_cd}, issue_task_idx = #{task_idx}
WHERE  infr_hist_no = #{hist_no}

UPDATE XAI_HIST
SET    xai_sttus_cd = #{sttus_cd}
WHERE  xai_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    eps = #{eps}
WHERE  learn_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    ml_result_json = #{result}
WHERE  learn_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    start_time = #{curr_time}
WHERE  learn_hist_no = #{hist_no}

UPDATE LEARN_HIST
SET    end_time = #{curr_time}
WHERE  learn_hist_no = #{hist_no}

UPDATE INFERENCE_HIST
SET    start_time = #{curr_time}
WHERE  infr_hist_no = #{hist_no}

UPDATE INFERENCE_HIST
SET    end_time = #{curr_time}
WHERE  infr_hist_no = #{hist_no}

UPDATE DATASET
SET    status_cd = #{status_cd}
WHERE  dataset_id = #{dataset_id}

UPDATE LEARN_HIST
SET    eval_result_json = #{result}
WHERE  learn_hist_no = #{hist_no}

UPDATE INFERENCE_HIST
SET    logs = #{logs}
WHERE  infr_hist_no = #{infr_hist_no}

UPDATE INFERENCE_HIST
SET    infr_accuracy = #{infr_accuracy}, infr_f1score = #{infr_f1score}
WHERE  infr_hist_no = #{infr_hist_no}
