mapper.insert:
INSERT INTO PROJECT_INFO
(	project_id
,	project_purpose_cd
,	data_analysis_id
,   status
,   start_time
,   project_target_field
,   modeling_mode
,   early_stop_param
,   tag
)
VALUES
    (	#{project_id}
    ,	#{project_purpose_cd}
    ,	#{data_analysis_id}
    ,	#{status}
    ,	#{start_time}
    , #{project_target_field}
    , #{modeling_mode}
    , #{early_stop_param}
    , #{tag}
    )


  SELECT UUID_SHORT()

INSERT INTO EDA_INFO
(	eda_id
, status
, dp_analysis_id
, project_id
)
VALUES
  (	#{eda_id}
  , #{status}
  , #{dp_analysis_id}
  ,	#{project_id}
  )

INSERT INTO DP_ANLS_INFO
(	dp_analysis_id
,   project_id
,	data_analysis_json
,	data_analysis_id
)
VALUES
(	#{dp_analysis_id}
,   #{project_id}
,	#{data_analysis_json}
,	#{data_analysis_id}
)

INSERT INTO ALG_ANAL_INFO
(	alg_anal_id
,   project_id
,	alg_id
,   dp_analysis_id
,	metadata_json
,   alg_type
,   alg_json
,   algorithm_code
)
VALUES
(	#{alg_anal_id}
,   #{project_id}
,	#{alg_id}
,   #{dp_analysis_id}
,	#{metadata_json}
,	#{alg_type}
,	#{alg_json}
,	#{algorithm_code}
)

INSERT INTO ML_PARAM_INFO
(	param_id
,   alg_id
,   alg_anal_id
,   dp_analysis_id
,	project_id
,	param_json
)
VALUES
(	#{param_id}
,   #{alg_id}
,   #{alg_anal_id}
,   #{dp_analysis_id}
,	#{project_id}
,	#{param_json}
)

INSERT INTO LEARN_HIST
(	learn_hist_no
,   param_id
,   alg_anal_id
,   alg_id
,   dp_analysis_id
,	project_id
,   learn_sttus_cd
,   start_time
,   eval_result_json
)
VALUES
(	#{learn_hist_no}
,   #{param_id}
,   #{alg_anal_id}
,   #{alg_id}
,   #{dp_analysis_id}
,	#{project_id}
,   #{learn_sttus_cd}
,   #{start_time}
,   '{}'
)

mapper.select:
SELECT learn_hist_no, learn_sttus_cd
FROM LEARN_HIST
WHERE project_id = #{project_id}

SELECT data_analysis_id
FROM DATA_ANLS_INFO
WHERE dataset_id = #{dataset_id}

SELECT *
FROM DP_ANLS_INFO
WHERE dp_analysis_id = #{dp_analysis_id}
AND project_id = #{project_id}

SELECT *
FROM ML_PARAM_INFO
WHERE project_id = #{project_id}
AND dp_analysis_id = #{dp_analysis_id}
AND alg_anal_id = #{alg_anal_id}
AND param_id = #{param_id}

SELECT project_target_field
FROM PROJECT_INFO
WHERE project_id = #{project_id}

SELECT project_id
FROM LEARN_HIST
WHERE learn_hist_no = #{histNo}

SELECT logs
FROM LEARN_HIST
WHERE learn_hist_no = #{histNo}

SELECT learn_hist_no, learn_sttus_cd
FROM LEARN_HIST
WHERE project_id = #{projectId}

SELECT tag
FROM PROJECT_INFO
WHERE project_id = #{projectId}

mapper.update:
UPDATE PROJECT_INFO
SET    status = #{status}
WHERE  project_id = #{project_id}

UPDATE EDA_INFO
SET    status = #{status}
WHERE  eda_id = #{eda_id}

UPDATE ML_PARAM_INFO
SET    used_yn = 'Y'
WHERE  param_id = #{param_id}

UPDATE LEARN_HIST
SET    logs = #{logs}
WHERE  learn_hist_no = #{learn_hist_no}

UPDATE LEARN_HIST
SET    model_bookmark = #{bookmarkYN}
WHERE  learn_hist_no = #{learn_hist_no}

UPDATE LEARN_HIST
SET    model_alias = #{model_alias}
WHERE  learn_hist_no = #{learn_hist_no}

UPDATE LEARN_HIST
SET    select_yn = 'N'
WHERE  project_id = #{project_id}

UPDATE LEARN_HIST
SET    select_yn = 'Y'
WHERE  learn_hist_no = #{learn_hist_no}
